tion&nbsp;(Vector3.forward*bullspeed);<br />\r\n	59&nbsp;cun_time=0;<br />\r\n	60&nbsp;}<br />\r\n	61&nbsp;}<br />\r\n	62&nbsp;if((Vector3.Distance(transform.position,&nbsp;target.position)&nbsp;&lt;&nbsp;attackRange))&nbsp;//发现目标而且在范围之内，开始追击目标<br />\r\n	63&nbsp;{<br />\r\n	64&nbsp;var&nbsp;targetPoint1&nbsp;=&nbsp;target.position;<br />\r\n	65&nbsp;//Calculation&nbsp;the&nbsp;angle&nbsp;with&nbsp;target<br />\r\n	66&nbsp;var&nbsp;targetRotation1&nbsp;=&nbsp;Quaternion.LookRotation&nbsp;(targetPoint1&nbsp;-&nbsp;transform.position,Vector3.up);<br />\r\n	67&nbsp;//Rotated&nbsp;Itself&nbsp;then&nbsp;make&nbsp;a&nbsp;line&nbsp;with&nbsp;target<br />\r\n	68&nbsp;transform.rotation&nbsp;=&nbsp;Quaternion.Slerp(transform.rotation,&nbsp;targetRotation1,&nbsp;Time.deltaTime&nbsp;*&nbsp;1.0);<br />\r\n	69&nbsp;transform.Translate(Vector3.forward*0.05);<br />\r\n	70&nbsp;}<br />\r\n	71&nbsp;}<br />\r\n	72&nbsp;function&nbsp;CanSeeTarget&nbsp;():&nbsp;System.Boolean&nbsp;//计算是否被发现<br />\r\n	73&nbsp;{<br />\r\n	74&nbsp;if&nbsp;(Vector3.Distance(transform.position,&nbsp;target.position)&nbsp;&gt;&nbsp;attackRange)&nbsp;return&nbsp;false;<br />\r\n	75&nbsp;var&nbsp;hit&nbsp;:&nbsp;RaycastHit;&nbsp;if&nbsp;(Physics.Linecast&nbsp;(transform.position,&nbsp;target.position,&nbsp;hit))<br />\r\n	76&nbsp;return&nbsp;hit.transform&nbsp;==&nbsp;target;&nbsp;return&nbsp;false;<br />\r\n	77&nbsp;}<br />\r\n	复制代码</td>\r\n	</tr>\r\n</tbody>\r\n</table>\r\n<br />\r\n','0','','0','10000','','0','','0','1','|0');
