&nbsp;yQuaternion&nbsp;=&nbsp;Quaternion.AngleAxis&nbsp;(rotationY,&nbsp;Vector3.left);<br />\r\n	30&nbsp;transform.localRotation&nbsp;=&nbsp;originalRotation&nbsp;*&nbsp;yQuaternion;<br />\r\n	31&nbsp;}<br />\r\n	32&nbsp;}<br />\r\n	33&nbsp;}<br />\r\n	复制代码</td>\r\n	</tr>\r\n</tbody>\r\n</table>\r\n<br />\r\n','0','','0','10000','','0','','0','1','|0');
INSERT INTO `v9_news_data` VALUES('45','<table>\r\n<tbody>\r\n	<tr>\r\n	<td style=\"width:577px;\">用途：<br />\r\n	一个GUI对象在屏幕中飞行，用GameObject.SendMessage告诉GUI对象何时飞行。<br />\r\n	使用：<br />\r\n	把该脚本拖到一个GameObject上，用另一个脚本GameObject.SendMessage<br />\r\n	发送飞行消息：<br />\r\n	脚本如下：&nbsp;<br />\r\n	1&nbsp;//Attach&nbsp;this&nbsp;to&nbsp;the&nbsp;same&nbsp;as&nbsp;the&nbsp;GUIFly&nbsp;is&nbsp;attached&nbsp;to.<br />\r\n	2&nbsp;//&nbsp;Fly&nbsp;in<br />\r\n	3&nbsp;gameObject.SendMessage(&quot;Fly&quot;,&nbsp;true);<br />\r\n	4&nbsp;//&nbsp;Wait&nbsp;5&nbsp;seconds<br />\r\n	5&nbsp;yield&nbsp;new&nbsp;WaitForSeconds(5);<br />\r\n	6&nbsp;//&nbsp;Fly&nbsp;out<br />\r\n	7&nbsp;gameObject.SendMessage(&quot;Fly&quot;,&nbsp;false);<br />\r\n	复制代码<br />\r\n	8&nbsp;using&nbsp;UnityEngine;<br />\r\n	9&nbsp;using&nbsp;System.Collections;<br />\r\n	10&nbsp;public&nbsp;class&nbsp;GUIFly&nbsp;:&nbsp;MonoBehaviour<br />\r\n	11&nbsp;{<br />\r\n	12&nbsp;public&nbsp;enum&nbsp;InterpolationType<br />\r\n	13&nbsp;{<br />\r\n	14&nbsp;Linear,<br />\r\n	15&nbsp;Sinusoidal,<br />\r\n	16&nbsp;Hermite<br />\r\n	17&nbsp;}<br />\r\n	18&nbsp;<br />\r\n	19&nbsp;public&nbsp;Vector3&nbsp;m_InPosition;<br />\r\n	20&nbsp;public&nbsp;Vector3&nbsp;m_OutPosition;<br />\r\n	21&nbsp;public&nbsp;float&nbsp;m_TravelTime&nbsp;=&nbsp;0.5f;<br />\r\n	22&nbsp;public&nbsp;float&nbsp;m_DelayToStartTravelingAfterMessageReceived&nbsp;=&nbsp;0.1f;<br />\r\n	23&nbsp;public&nbsp;bool&nbsp;m_StartWithInPosition&nbsp;=&nbsp;false;<br />\r\n	24&nbsp;public&nbsp;InterpolationType&nbsp;m_InterpolationType&nbsp;=&nbsp;InterpolationType.Sinusoidal;<br />\r\n	25&nbsp;<br />\r\n	26&nbsp;void&nbsp;Start&nbsp;()<br />\r\n	27&nbsp;{<br />\r\n	28&nbsp;transform.position&nbsp;=&nbsp;(m_StartWithInPosition)&nbsp;?&nbsp;m_InPosition&nbsp;:&nbsp;m_OutPosition;<br />\r\n	29&nbsp;}<br />\r\n	30&nbsp;<br />\r\n	31&nbsp;IEnumerator&nbsp;Fly(bool&nbsp;flyIn)<br />\r\n	32&nbsp;{<br />\r\n	33&nbsp;yield&nbsp;return&nbsp;new&nbsp;WaitForSeconds(m_DelayToStartTravelingAfterMessageReceived);<br />\r\n	34&nbsp;<br />\r\n	35&nbsp;Vector3&nbsp;targetPosition&nbsp;=&nbsp;(flyIn)&nbsp;?&nbsp;m_InPosition&nbsp;:&nbsp;m_OutPosition;<br />\r\n	36&nbsp;float&nbsp;startTime&nbsp;=&nbsp;Time.time;<br />\r\n	37&nbsp;Vector3&nbsp;startPosition&nbsp;=&nbsp;transform.position;<br />\r\n	38&nbsp;<br />\r\n	39&nbsp;while&nbsp;(Time.time&nbsp;&lt;&nbsp;startTime&nbsp;+&nbsp;m_TravelTime)<br />\r\n	40&nbsp;{<br />\r\n	41&nbsp;switch&nbsp;(m_InterpolationType)<br />\r\n	42&nbsp;{<br />\r\n	43&nbsp;case&nbsp;InterpolationType.Linear:<br />\r\n	44&nbsp;transform.position&nbsp;=&nbsp;Vector3.Lerp(startPosition,&nbsp;targetPosition,&nbsp;(Time.time&nbsp;-&nbsp;startTime)&nbsp;/&nbsp;m_TravelTime);<br />\r\n	45&nbsp;break;<br />\r\n	46&nbsp;case&nbsp;InterpolationType.Sinusoidal:<br />\r\n	47&nbsp;transform.position&nbsp;=&nbsp;Sinerp(startPosition,&nbsp;targetPosition,&nbsp;(Time.time&nbsp;-&nbsp;startTime)&nbsp;/&nbsp;m_TravelTime);<br />\r\n	48&nbsp;break;<br />\r\n	49&nbsp;case&nbsp;InterpolationType.Hermite:<br />\r\n	50&nbsp;transform.position&nbsp;=&nbsp;Hermite(startPosition,&nbsp;targetPosition,&nbsp;(Time.time&nbsp;-&nbsp;startTime)&nbsp;/&nbsp;m_TravelTime);<br />\r\n	51&nbsp;break;<br />\r\n	52&nbsp;}<br />\r\n	53&nbsp;yield&nbsp;return&nbsp;0;<br />\r\n	54&nbsp;}<br />\r\n	55&nbsp;<br />\r\n	56&nbsp;transform.position&nbsp;=&nbsp;targetPosition;<br />\r\n	57&nbsp;}<br />\r\n	58&nbsp;<br />\r\n	59&nbsp;void&nbsp;Reset()<br />\r\n	60&nbsp;{<br />\r\n	61&nbsp;m_InPosition&nbsp;=&nbsp;transform.position;<br />\r\n	62&nbsp;}<br />\r\n	63&nbsp;private&nbsp;static&nbsp;Vector3&nbsp;Sinerp(Vector3&nbsp;start,&nbsp;Vector3&nbsp;end,&nbsp;float&nbsp;value)<br />\r\n	64&nbsp;{<br />\r\n	65&nbsp;return&nbsp;new&nbsp;Vector3(Sinerp(start.x,&nbsp;end.x,&nbsp;value),&nbsp;Sinerp(start.y,&nbsp;end.y,&nbsp;value),&nbsp;Sinerp(start.z,&nbsp;end.z,&nbsp;value));<br />\r\n	66&nbsp;}<br />\r\n	67&nbsp;<br />\r\n	68&nbsp;private&nbsp;static&nbsp;Vector3&nbsp;Hermite(Vector3&nbsp;start,&nbsp;Vector3&nbsp;end,&nbsp;float&nbsp;value)<br />\r\n	69&nbsp;{<br />\r\n	70&nbsp;return&nbsp;new&nbsp;Vector3(Hermite(start.x,&nbsp;end.x,&nbsp;value),&nbsp;Hermite(start.y,&nbsp;end.y,&nbsp;value),&nbsp;Hermite(start.z,&nbsp;end.z,&nbsp;value));<br />\r\n	71&nbsp;}<br />\r\n	72&nbsp;<br />\r\n	73&nbsp;/*&nbsp;The&nbsp;following&nbsp;functions&nbsp;are&nbsp;also&nbsp;in&nbsp;the&nbsp;Mathfx&nbsp;script&nbsp;on&nbsp;the&nbsp;UnifyWiki,&nbsp;but&nbsp;are&nbsp;included&nbsp;here&nbsp;so&nbsp;the&nbsp;script&nbsp;is&nbsp;self&nbsp;sufficient.&nbsp;*/<br />\r\n	74&nbsp;<br />\r\n	75&nbsp;private&nbsp;static&nbsp;float&nbsp;Sinerp(float&nbsp;start,&nbsp;float&nbsp;end,&nbsp;float&nbsp;value)<br />\r\n	76&nbsp;{<br />\r\n	77&nbsp;return&nbsp;Mathf.Lerp(start,&nbsp;end,&nbsp;Mathf.Sin(value&nbsp;*&nbsp;Mathf.PI&nbsp;*&nbsp;0.5f));<br />\r\n	78&nbsp;}<br />\r\n	79&nbsp;private&nbsp;static&nbsp;float&nbsp;Hermite(float&nbsp;start,&nbsp;float&nbsp;end,&nbsp;float&nbsp;value)<br />\r\n	80&nbsp;{<br />\r\n	81&nbsp;return&nbsp;Mathf.Lerp(start,&nbsp;end,&nbsp;value&nbsp;*&nbsp;value&nbsp;*&nbsp;(3.0f&nbsp;-&nbsp;2.0f&nbsp;*&nbsp;value));<br />\r\n	82&nbsp;}<br />\r\n	83&nbsp;}<br />\r\n	复制代码</td>\r\n	</tr>\r\n</tbody>\r\n</table>\r\n<br />\r\n','0','','0','10000','','0','','0','1','|0');
INSERT INTO `v9_news_data` VALUES('46','<table>\r\n<tbody>\r\n	<tr>\r\n	<td style=\"width:577px;\">Unity3d导入3dMax模型会产生若干问题，按照官方的说明，将max模型导成fbx文件导入untiy似乎也不能解决<br />\r\n	1、x轴向偏转<br />\r\n	3dmax模型导入后自动有一个x轴270度的偏转，巧合的是，在unity中旋转模型的时候，你会发现y轴参照方向永远朝上，而x和z轴则以模型本身的局部坐标为准，这样当模型沿x轴旋转270度之后，z轴正好与y轴重合，这样你试图用程序控制方向的时候就会发现旋转y和旋转z效果相同，这显然不是你期望的结果。解决这个问题的方法是，将.max文件导出为.3ds文件再导入untiy，而不是官方说的导成.fbx<br />\r\n	2、材质问题<br />\r\n	模型在3dmax里赋予材质之后，不管通过何种方式导入到unity中，都是没有材质的，好在untiy自动生成了这些材质的材质球，我们需要做的是将贴图再重新设置一遍，如果你的材质还有其他效果，比如lightmap，则需要选择正确的shader之后再设置贴图。<br />\r\n	另外一个是多重材质，多重材质暂时还不知道如何在unity中显示，所以在制作模型的之后，应该先确定哪些部件应该合在一起，而哪些需要独立，比如，对于一架直升飞机来说，身体和螺旋桨就可以分为两个部件，而不应该把螺旋桨与发动机合并成一个部件，这样程序就无法旋转螺旋桨了。<br />\r\n	3、缩放因子问题<br />\r\n	模型导入到untiy中，如果你之前在3dmax中没有关心过&ldquo;单位&rdquo;问题，则它在场景里的大小一定会出呼你的意料。<br />\r\n	unity中的1单位是fbx文件中的1单位的100倍<br />\r\n	所以，如果我们想unity中使用1单位=1米的话，那在max和maya中制作的时候，单位就设置成1厘米<br />\r\n	如果我们想unity中1单位=100M，那单位就设置为1M<br />\r\n	如果我们想unity中1单位=1厘米的话，那单位就设置为0.1毫米，或者在单位为1厘米的情况下把比例因子改为1<br />\r\n	在max中，单设置应该像这样<br />\r\n	<br />\r\n	1Unit&nbsp;=&nbsp;1厘米，这样在unity中1格就等3dmax中的1M了<br />\r\n	针对经常出现的问题，将需要注意的点罗列如下：<br />\r\n	1、物体的头要朝下<br />\r\n	2、所有游戏中的车辆飞机大致都可以分成两个部分，一个是身体，一个是炮台或螺旋桨，那么身体要有一个统一的名称，都叫做Body，螺旋桨叫做Airscrew，炮台叫做&nbsp;Barbette&nbsp;，这样我就可以找到所有的身体，并把爆炸效果的火焰和身体连接起来<br />\r\n	3、模型尺寸比例问题，参考前文所说的设置，另外不管是飞机，还是坦克，尺寸都在10米左右（以这个尺寸为基准），也就是3dmax里的10格大小<br />\r\n	4、模型要在场景中居中，否则导入物体质心位置不对<br />\r\n	5、起名字可以起这个武器的实际名称，如果不知道的话，也可以叫做Tank1，Tank2这样，但最重要的是贴图名字和模型名字要相同，否则很乱</td>\r\n	</tr>\r\n</tbody>\r\n</table>\r\n<br />\r\n','0','','0','10000','','0','','0','1','|0');
